import React, { useState, useEffect, useRef } from "react";
import { Mic, Star, Users, Zap, ArrowRight } from "lucide-react";
import { motion, AnimatePresence } from "framer-motion";

// Particle Background
const ParticleBackground = () => {
  const [particles, setParticles] = useState([]);

  useEffect(() => {
    const generateParticles = () => {
      const newParticles = [];
      for (let i = 0; i < 150; i++) {
        newParticles.push({
          id: i,
          x: Math.random() * 100,
          y: Math.random() * 100,
          size: Math.random() * 3 + 1,
          opacity: Math.random() * 0.8 + 0.2,
          speed: Math.random() * 2 + 0.5,
          direction: Math.random() * 2 * Math.PI,
        });
      }
      setParticles(newParticles);
    };

    generateParticles();
    const interval = setInterval(() => {
      setParticles((prev) =>
        prev.map((p) => {
          let newX = p.x + Math.cos(p.direction) * p.speed * 0.1;
          let newY = p.y + Math.sin(p.direction) * p.speed * 0.1;
          if (newX > 100) newX = 0;
          if (newX < 0) newX = 100;
          if (newY > 100) newY = 0;
          if (newY < 0) newY = 100;
          return { ...p, x: newX, y: newY };
        })
      );
    }, 100);

    return () => clearInterval(interval);
  }, []);

  return (
    <div className="absolute inset-0 overflow-hidden">
      {particles.map((p) => (
        <div
          key={p.id}
          className="absolute bg-blue-400 rounded-full animate-pulse"
          style={{
            left: `${p.x}%`,
            top: `${p.y}%`,
            opacity: p.opacity,
            width: `${p.size}px`,
            height: `${p.size}px`,
          }}
        />
      ))}
    </div>
  );
};

export default function App() {
  const [searchOpen, setSearchOpen] = useState(false);
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState("");
  const messagesEndRef = useRef(null);

  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages]);

  const sendMessage = async () => {
    if (!input.trim()) return;
    const userMessage = { from: "user", text: input };
    setMessages((prev) => [...prev, userMessage]);
    const question = input;
    setInput("");

    try {
      const res = await fetch("http://localhost:8000/ask", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ question }),
      });
      const data = await res.json();
      const botReply = data.answer || data.error || "No response";
      setMessages((prev) => [...prev, { from: "bot", text: botReply }]);
    } catch {
      setMessages((prev) => [
        ...prev,
        { from: "bot", text: "Error connecting to backend." },
      ]);
    }
  };

  return (
    <div className="min-h-screen bg-black text-white overflow-hidden relative">
      <ParticleBackground />
      <div className="absolute inset-0 bg-gradient-to-br from-blue-900/20 via-purple-900/20 to-pink-900/20" />

      <div className="relative z-10 flex flex-col items-center justify-center min-h-[80vh] px-6 text-center">
        <h1 className="text-5xl lg:text-7xl font-black leading-tight mb-8">
          Your Personal Work Assistant
        </h1>

        {/* Animated Search Button → Search Bar */}
        <motion.div layout className="flex flex-col items-center space-y-6">
          {!searchOpen ? (
            <motion.button
              layout
              onClick={() => setSearchOpen(true)}
              className="px-8 py-4 bg-gradient-to-r from-purple-600 to-pink-600 rounded-full text-lg font-semibold hover:shadow-2xl hover:scale-105 transition-all flex items-center space-x-2"
            >
              <span>Search</span>
              <ArrowRight className="w-5 h-5" />
            </motion.button>
          ) : (
            <motion.div
              layout
              initial={{ scale: 0.8, opacity: 0 }}
              animate={{ scale: 1, opacity: 1 }}
              transition={{ type: "spring", stiffness: 200 }}
              className="flex w-full max-w-xl"
            >
              <input
                type="text"
                value={input}
                onChange={(e) => setInput(e.target.value)}
                onKeyDown={(e) => e.key === "Enter" && sendMessage()}
                placeholder="Type your query..."
                className="flex-1 p-3 rounded-l-lg border-none outline-none text-black"
              />
              <button
                onClick={sendMessage}
                className="bg-blue-500 text-white px-6 rounded-r-lg hover:bg-blue-600 transition-colors"
              >
                Go
              </button>
            </motion.div>
          )}
        </motion.div>

        {/* Messages Display */}
        <div className="flex flex-col bg-gray-900 rounded-lg p-4 h-[40vh] overflow-y-auto mt-8 w-full max-w-xl">
          <AnimatePresence initial={false}>
            {messages.map((msg, i) => (
              <motion.div
                key={i}
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                exit={{ opacity: 0, y: -20 }}
                className={`mb-3 ${msg.from === "user" ? "self-end" : "self-start"}`}
              >
                <div
                  className={`px-4 py-2 rounded-lg max-w-[80%] break-words ${
                    msg.from === "user"
                      ? "bg-blue-500 text-white"
                      : "bg-gray-700 text-gray-100"
                  }`}
                >
                  {msg.text}
                </div>
              </motion.div>
            ))}
          </AnimatePresence>
          <div ref={messagesEndRef} />
        </div>
      </div>
    </div>
  );
}
