import React, { useState, useEffect, useRef } from 'react';
import { Mic, Star, Users, Zap, ArrowRight, Search, Send } from 'lucide-react';

const ParticleBackground = () => {
  const [particles, setParticles] = useState([]);

  useEffect(() => {
    const generateParticles = () => {
      const newParticles = [];
      for (let i = 0; i < 150; i++) {
        newParticles.push({
          id: i,
          x: Math.random() * 100,
          y: Math.random() * 100,
          size: Math.random() * 3 + 1,
          opacity: Math.random() * 0.8 + 0.2,
          speed: Math.random() * 2 + 0.5,
          direction: Math.random() * 2 * Math.PI,
        });
      }
      setParticles(newParticles);
    };

    generateParticles();

    const interval = setInterval(() => {
      setParticles(prev =>
        prev.map(particle => {
          let newX = particle.x + Math.cos(particle.direction) * particle.speed * 0.1;
          let newY = particle.y + Math.sin(particle.direction) * particle.speed * 0.1;

          if (newX > 100) newX = 0;
          if (newX < 0) newX = 100;
          if (newY > 100) newY = 0;
          if (newY < 0) newY = 100;

          return { ...particle, x: newX, y: newY };
        })
      );
    }, 100);

    return () => clearInterval(interval);
  }, []);

  return (
    <div className="absolute inset-0 overflow-hidden">
      {particles.map(particle => (
        <div
          key={particle.id}
          className="absolute bg-blue-400 rounded-full animate-pulse"
          style={{
            left: `${particle.x}%`,
            top: `${particle.y}%`,
            opacity: particle.opacity,
            width: `${particle.size}px`,
            height: `${particle.size}px`,
          }}
        />
      ))}
    </div>
  );
};

const FeatureCard = ({ icon, title, description, color = 'blue' }) => {
  const colorClasses = {
    blue: 'from-blue-500/20 to-purple-600/20 border-blue-500/30',
    purple: 'from-purple-500/20 to-pink-600/20 border-purple-500/30',
    pink: 'from-pink-500/20 to-red-600/20 border-pink-500/30',
  };

  return (
    <div
      className={`relative p-6 rounded-2xl backdrop-blur-lg bg-gradient-to-br ${colorClasses[color]} border border-white/10 hover:border-white/20 transition-all duration-300 hover:scale-105 hover:shadow-2xl group`}
    >
      <div className="flex items-center mb-4">
        <div
          className={`p-3 rounded-xl bg-gradient-to-r ${color === 'blue'
            ? 'from-blue-500 to-purple-600'
            : color === 'purple'
              ? 'from-purple-500 to-pink-600'
              : 'from-pink-500 to-red-600'
            } shadow-lg`}
        >
          {icon}
        </div>
      </div>
      <h3 className="text-xl font-bold text-white mb-2 group-hover:text-transparent group-hover:bg-clip-text group-hover:bg-gradient-to-r group-hover:from-blue-400 group-hover:to-purple-400 transition-all duration-300">
        {title}
      </h3>
      <p className="text-gray-300 leading-relaxed">{description}</p>
    </div>
  );
};

const UserAvatar = ({ src, className = '' }) => (
  <div
    className={`w-12 h-12 rounded-full border-2 border-white/50 overflow-hidden shadow-lg ${className}`}
  >
    <div className="w-full h-full bg-gradient-to-br from-blue-400 to-purple-600 flex items-center justify-center text-white font-bold">
      {src || 'U'}
    </div>
  </div>
);

function App() {
  const [isHovered, setIsHovered] = useState(false);
  const [isSearchExpanded, setIsSearchExpanded] = useState(false);
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState("");
  const messagesEndRef = useRef(null);

  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages]);

  const handleSearchClick = () => {
    setIsSearchExpanded(true);
  };

  const sendMessage = async () => {
    if (!input.trim()) return;

    const userMessage = { from: "user", text: input };
    setMessages((prev) => [...prev, userMessage]);
    const question = input;
    setInput("");

    try {
      const response = await fetch("http://localhost:8000/ask", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ question }),
      });
      const data = await response.json();
      const botReply = data.answer || data.error || "No response";

      setMessages((prev) => [...prev, { from: "bot", text: botReply }]);
    } catch (error) {
      setMessages((prev) => [
        ...prev,
        { from: "bot", text: "Error connecting to backend." },
      ]);
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      sendMessage();
    }
  };

  return (
    <div className="min-h-screen bg-black text-white overflow-hidden relative">
      {/* Particle Background */}
      <ParticleBackground />

      {/* Gradient Overlays */}
      <div className="absolute inset-0 bg-gradient-to-br from-blue-900/20 via-purple-900/20 to-pink-900/20" />
      <div className="absolute top-0 left-1/4 w-96 h-96 bg-blue-500/10 rounded-full blur-3xl" />
      <div className="absolute bottom-0 right-1/4 w-96 h-96 bg-purple-500/10 rounded-full blur-3xl" />

      {/* Navigation */}
      <nav className="relative z-50 flex items-center justify-between p-6 lg:px-12">
        <div className="text-2xl font-bold bg-gradient-to-r from-blue-400 to-purple-400 bg-clip-text text-transparent">
          FLUTTERTOP
        </div>
        <div className="hidden md:flex items-center space-x-8">
          <a href="#" className="hover:text-blue-400 transition-colors">
            Home page
          </a>
          <a href="#" className="hover:text-blue-400 transition-colors">
            About us
          </a>
          <a href="#" className="hover:text-blue-400 transition-colors">
            Pricing plans
          </a>
          <a href="#" className="hover:text-blue-400 transition-colors">
            Contact us
          </a>
        </div>
      </nav>

      {/* Main Content */}
      <div className="relative z-10 flex flex-col items-center justify-center min-h-[80vh] px-6 text-center">
        {!isSearchExpanded ? (
          <>
            {/* Central Visualization */}
            <div className="relative mb-12">
              <div className="relative w-80 h-80 lg:w-96 lg:h-96 mx-auto">
                <div
                  className="absolute inset-0 rounded-full border-2 border-dashed border-blue-500/30 animate-spin"
                  style={{ animationDuration: '20s' }}
                >
                  {[...Array(8)].map((_, i) => (
                    <div
                      key={i}
                      className="absolute w-2 h-2 bg-blue-400 rounded-full"
                      style={{
                        top: '50%',
                        left: '50%',
                        transform: `translate(-50%, -50%) rotate(${i * 45}deg) translateY(-160px)`,
                      }}
                    />
                  ))}
                </div>

                <div className="absolute inset-8 rounded-full bg-gradient-to-br from-blue-600/20 to-purple-600/20 backdrop-blur-xl border border-white/10 flex items-center justify-center">
                  <div className="text-center space-y-4">
                    <div className="w-16 h-16 mx-auto bg-gradient-to-r from-orange-400 to-pink-500 rounded-full flex items-center justify-center shadow-lg">
                      <Zap className="w-8 h-8 text-white" />
                    </div>
                    <div className="text-sm font-semibold text-blue-300">Powered by Deepseek</div>
                  </div>
                </div>

                <div className="absolute bottom-8 left-16 flex -space-x-2">
                  <div className="text-xs text-gray-300 ml-4 self-center">
                  </div>
                </div>
              </div>
            </div>

            {/* Hero Text */}
            <div className="max-w-4xl mx-auto space-y-6 mb-12">
              <h1 className="text-5xl lg:text-7xl font-black leading-tight">
                <span className="bg-gradient-to-r from-white via-blue-200 to-purple-300 bg-clip-text text-transparent">
                  Your Personal
                </span>
                <br />
                <span className="bg-gradient-to-r from-blue-400 via-purple-400 to-pink-400 bg-clip-text text-transparent">
                  Work Assistant
                </span>
              </h1>

              <p className="text-xl text-gray-400 max-w-2xl mx-auto leading-relaxed">
                Experience the future of conversation with our advanced AI that understands context,
                emotion, and responds with remarkable human-like intelligence.
              </p>
            </div>

            {/* Animated Search Button */}
            <div className="relative group">
              <button
                onClick={handleSearchClick}
                className="relative px-8 py-4 bg-gradient-to-r from-purple-600 to-pink-600 rounded-full text-lg font-semibold hover:shadow-2xl hover:shadow-purple-500/25 transition-all duration-500 hover:scale-105 flex items-center space-x-2 transform"
                onMouseEnter={() => setIsHovered(true)}
                onMouseLeave={() => setIsHovered(false)}
              >
                <Search className="w-5 h-5" />
                <span>Search</span>
                <div className={`transition-transform duration-300 ${isHovered ? 'translate-x-1' : ''}`}>
                  <ArrowRight className="w-5 h-5" />
                </div>
              </button>
              <div className="absolute inset-0 bg-gradient-to-r from-purple-600 to-pink-600 rounded-full blur-xl opacity-20 group-hover:opacity-40 transition-opacity duration-300" />
            </div>
          </>
        ) : (
          /* Expanded Search Interface */
          <div className="w-full max-w-4xl mx-auto transition-all duration-700 ease-out">
            {/* Search Bar */}
            <div className="relative mb-8 transform transition-all duration-700 ease-out scale-100">
              <div className="flex bg-white/10 backdrop-blur-lg rounded-2xl border border-white/20 shadow-2xl">
                <input
                  type="text"
                  value={input}
                  onChange={(e) => setInput(e.target.value)}
                  onKeyPress={handleKeyPress}
                  placeholder="Ask me anything..."
                  className="flex-1 p-6 bg-transparent text-white text-lg placeholder-gray-400 outline-none rounded-l-2xl"
                  autoFocus
                />
                <button
                  onClick={sendMessage}
                  className="px-8 py-6 bg-gradient-to-r from-purple-600 to-pink-600 text-white rounded-r-2xl hover:shadow-lg transition-all duration-300 flex items-center justify-center group"
                >
                  <Send className="w-6 h-6 group-hover:translate-x-1 transition-transform duration-300" />
                </button>
              </div>
            </div>

            {/* Chat Messages */}
            {messages.length > 0 && (
              <div className="bg-white/5 backdrop-blur-lg rounded-2xl border border-white/10 p-6 max-h-96 overflow-y-auto transform transition-all duration-700 ease-out">
                <div className="space-y-4">
                  {messages.map((msg, i) => (
                    <div
                      key={i}
                      className={`flex ${msg.from === "user" ? "justify-end" : "justify-start"} animate-fade-in`}
                    >
                      <div
                        className={`max-w-xs lg:max-w-md px-4 py-3 rounded-2xl ${msg.from === "user"
                          ? "bg-gradient-to-r from-purple-600 to-pink-600 text-white"
                          : "bg-white/10 backdrop-blur-sm text-gray-100 border border-white/10"
                          } shadow-lg`}
                      >
                        <p className="text-sm leading-relaxed">{msg.text}</p>
                      </div>
                    </div>
                  ))}
                  <div ref={messagesEndRef} />
                </div>
              </div>
            )}

            {/* Back to Home Button */}
            <button
              onClick={() => {
                setIsSearchExpanded(false);
                setMessages([]);
                setInput("");
              }}
              className="mt-6 px-6 py-3 bg-white/10 backdrop-blur-sm border border-white/20 rounded-full text-gray-300 hover:text-white hover:bg-white/20 transition-all duration-300"
            >
              ‚Üê Back to Home
            </button>
          </div>
        )}
      </div>

      <style jsx>{`
        @keyframes fade-in {
          from {
            opacity: 0;
            transform: translateY(20px);
          }
          to {
            opacity: 1;
            transform: translateY(0);
          }
        }
        .animate-fade-in {
          animation: fade-in 0.5s ease-out;
        }
      `}</style>
    </div>
  );
}

export default App;
