import React, { useState, useEffect, useRef } from 'react';
import { Mic, Star, Users, Zap, ArrowRight, Search, Send } from 'lucide-react';

const ParticleBackground = () => {
  const [particles, setParticles] = useState([]);

  useEffect(() => {
    const generateParticles = () => {
      const newParticles = [];
      for (let i = 0; i < 150; i++) {
        newParticles.push({
          id: i,
          x: Math.random() * 100,
          y: Math.random() * 100,
          size: Math.random() * 3 + 1,
          opacity: Math.random() * 0.8 + 0.2,
          speed: Math.random() * 2 + 0.5,
          direction: Math.random() * 2 * Math.PI,
        });
      }
      setParticles(newParticles);
    };

    generateParticles();

    const interval = setInterval(() => {
      setParticles(prev =>
        prev.map(particle => {
          let newX = particle.x + Math.cos(particle.direction) * particle.speed * 0.1;
          let newY = particle.y + Math.sin(particle.direction) * particle.speed * 0.1;

          if (newX > 100) newX = 0;
          if (newX < 0) newX = 100;
          if (newY > 100) newY = 0;
          if (newY < 0) newY = 100;

          return { ...particle, x: newX, y: newY };
        })
      );
    }, 100);

    return () => clearInterval(interval);
  }, []);

  return (
    <div className="absolute inset-0 overflow-hidden">
      {particles.map(particle => (
        <div
          key={particle.id}
          className="absolute bg-blue-400 rounded-full animate-pulse"
          style={{
            left: `${particle.x}%`,
            top: `${particle.y}%`,
            opacity: particle.opacity,
            width: `${particle.size}px`,
            height: `${particle.size}px`,
          }}
        />
      ))}
    </div>
  );
};

function App() {
  const [isHovered, setIsHovered] = useState(false);
  const [showSearch, setShowSearch] = useState(false);
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState("");
  const messagesEndRef = useRef(null);

  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages]);

  const handleSearchClick = () => {
    setShowSearch(true);
  };

  const sendMessage = async () => {
    if (!input.trim()) return;

    const userMessage = { from: "user", text: input };
    setMessages((prev) => [...prev, userMessage]);
    const question = input;
    setInput("");

    try {
      const response = await fetch("http://localhost:8000/ask", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ question }),
      });
      const data = await response.json();
      const botReply = data.answer || data.error || "No response";

      setMessages((prev) => [...prev, { from: "bot", text: botReply }]);
    } catch (error) {
      setMessages((prev) => [
        ...prev,
        { from: "bot", text: "Error connecting to backend." },
      ]);
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      sendMessage();
    }
  };

  return (
    <div className="min-h-screen bg-black text-white overflow-hidden relative">
      {/* Particle Background */}
      <ParticleBackground />

      {/* Gradient Overlays */}
      <div className="absolute inset-0 bg-gradient-to-br from-blue-900/20 via-purple-900/20 to-pink-900/20" />
      <div className="absolute top-0 left-1/4 w-96 h-96 bg-blue-500/10 rounded-full blur-3xl" />
      <div className="absolute bottom-0 right-1/4 w-96 h-96 bg-purple-500/10 rounded-full blur-3xl" />

      {/* Navigation */}
      <nav className="relative z-50 flex items-center justify-between p-6 lg:px-12">
        <div className="text-2xl font-bold bg-gradient-to-r from-blue-400 to-purple-400 bg-clip-text text-transparent">
          FLUTTERTOP
        </div>
        <div className="hidden md:flex items-center space-x-8">
          <a href="#" className="hover:text-blue-400 transition-colors">
            Home page
          </a>
          <a href="#" className="hover:text-blue-400 transition-colors">
            About us
          </a>
          <a href="#" className="hover:text-blue-400 transition-colors">
            Pricing plans
          </a>
          <a href="#" className="hover:text-blue-400 transition-colors">
            Contact us
          </a>
        </div>
      </nav>

      {/* Debug Info */}
      <div className="fixed top-20 right-4 bg-black/70 text-white p-2 rounded text-xs z-50">
        Show Search: {showSearch ? 'TRUE' : 'FALSE'}
      </div>

      {/* Main Content */}
      <div className="relative z-10 flex flex-col items-center justify-center min-h-[80vh] px-6 text-center">

        {showSearch ? (
          /* SEARCH INTERFACE */
          <div className="w-full max-w-4xl mx-auto space-y-8 animate-fade-in">
            <div className="text-center">
              <h2 className="text-4xl font-bold bg-gradient-to-r from-blue-400 to-purple-400 bg-clip-text text-transparent mb-4">
                Ask Your Assistant
              </h2>
              <p className="text-gray-400 text-lg">What would you like to know?</p>
            </div>

            {/* SEARCH INPUT BOX */}
            <div className="relative">
              <div className="flex bg-white/10 backdrop-blur-lg rounded-3xl border border-white/20 shadow-2xl p-2">
                <div className="flex-1 relative">
                  <Search className="absolute left-6 top-1/2 transform -translate-y-1/2 text-gray-400 w-6 h-6" />
                  <input
                    type="text"
                    value={input}
                    onChange={(e) => setInput(e.target.value)}
                    onKeyPress={handleKeyPress}
                    placeholder="Type your question here..."
                    className="w-full pl-16 pr-6 py-6 bg-transparent text-white text-lg placeholder-gray-400 outline-none"
                    autoFocus
                  />
                </div>
                <button
                  onClick={sendMessage}
                  disabled={!input.trim()}
                  className="px-8 py-4 bg-gradient-to-r from-purple-600 to-pink-600 text-white rounded-2xl hover:shadow-lg transition-all duration-300 flex items-center justify-center group disabled:opacity-50 disabled:cursor-not-allowed hover:scale-105"
                >
                  <Send className="w-6 h-6 group-hover:translate-x-1 transition-transform duration-300" />
                  <span className="ml-2 hidden sm:block">Send</span>
                </button>
              </div>
            </div>

            {/* CHAT MESSAGES - Show below the search bar */}
            {showSearch && messages.length > 0 && (
              <div className="w-full max-w-4xl mx-auto mt-8 bg-white/5 backdrop-blur-lg rounded-3xl border border-white/10 p-8 max-h-96 overflow-y-auto animate-fade-in">
                <div className="space-y-6">
                  {messages.map((msg, i) => (
                    <div
                      key={i}
                      className={`flex ${msg.from === "user" ? "justify-end" : "justify-start"}`}
                    >
                      <div
                        className={`max-w-xs lg:max-w-2xl px-6 py-4 rounded-2xl ${msg.from === "user"
                            ? "bg-gradient-to-r from-purple-600 to-pink-600 text-white"
                            : "bg-white/10 backdrop-blur-sm text-gray-100 border border-white/10"
                          }`}
                      >
                        <p className="text-sm leading-relaxed">{msg.text}</p>
                      </div>
                    </div>
                  ))}
                  <div ref={messagesEndRef} />
                </div>
              </div>
            )}

            {/* BACK BUTTON - Only show when search is open */}
            {showSearch && (
              <div className="mt-8">
                <button
                  onClick={() => {
                    setShowSearch(false);
                    setMessages([]);
                    setInput("");
                  }}
                  className="px-8 py-3 bg-white/10 backdrop-blur-sm border border-white/20 rounded-full text-gray-300 hover:text-white hover:bg-white/20 transition-all duration-300"
                >
                  ‚Üê Back to Home
                </button>
              </div>
            )}
          </div>
        ) : (
          /* LANDING PAGE */
          <>
            {/* Central Visualization */}
            <div className="relative mb-12">
              <div className="relative w-80 h-80 lg:w-96 lg:h-96 mx-auto">
                <div
                  className="absolute inset-0 rounded-full border-2 border-dashed border-blue-500/30 animate-spin"
                  style={{ animationDuration: '20s' }}
                >
                  {[...Array(8)].map((_, i) => (
                    <div
                      key={i}
                      className="absolute w-2 h-2 bg-blue-400 rounded-full"
                      style={{
                        top: '50%',
                        left: '50%',
                        transform: `translate(-50%, -50%) rotate(${i * 45}deg) translateY(-160px)`,
                      }}
                    />
                  ))}
                </div>

                <div className="absolute inset-8 rounded-full bg-gradient-to-br from-blue-600/20 to-purple-600/20 backdrop-blur-xl border border-white/10 flex items-center justify-center">
                  <div className="text-center space-y-4">
                    <div className="w-16 h-16 mx-auto bg-gradient-to-r from-orange-400 to-pink-500 rounded-full flex items-center justify-center shadow-lg">
                      <Zap className="w-8 h-8 text-white" />
                    </div>
                    <div className="text-sm font-semibold text-blue-300">Powered by Deepseek</div>
                  </div>
                </div>
              </div>
            </div>

            {/* Hero Text */}
            <div className="max-w-4xl mx-auto space-y-6 mb-12">
              <h1 className="text-5xl lg:text-7xl font-black leading-tight">
                <span className="bg-gradient-to-r from-white via-blue-200 to-purple-300 bg-clip-text text-transparent">
                  Your Personal
                </span>
                <br />
                <span className="bg-gradient-to-r from-blue-400 via-purple-400 to-pink-400 bg-clip-text text-transparent">
                  Work Assistant
                </span>
              </h1>

              <p className="text-xl text-gray-400 max-w-2xl mx-auto leading-relaxed">
                Experience the future of conversation with our advanced AI that understands context,
                emotion, and responds with remarkable human-like intelligence.
              </p>
            </div>

            {/* EXPANDING SEARCH BUTTON */}
            <div className="relative">
              <div
                className={`flex items-center bg-gradient-to-r from-purple-600 to-pink-600 rounded-full shadow-2xl overflow-hidden transition-all duration-500 ease-in-out ${
                  showSearch ? 'w-96 px-4' : 'w-48 px-0'
                }`}
              >
                {showSearch && (
                  <input
                    type="text"
                    value={input}
                    onChange={e => setInput(e.target.value)}
                    onKeyPress={handleKeyPress}
                    className="flex-1 bg-transparent outline-none text-white placeholder-white/70 px-4 py-4 text-lg"
                    placeholder="Ask me anything..."
                    autoFocus
                  />
                )}
                <button
                  onClick={() => {
                    if (showSearch && input.trim()) {
                      sendMessage();
                    } else {
                      setShowSearch(!showSearch);
                    }
                  }}
                  className="flex items-center justify-center w-12 h-12 text-white hover:bg-white/20 transition-all duration-300 rounded-full m-2"
                >
                  {showSearch ? (
                    input.trim() ? <Send size={20} /> : <X size={20} />
                  ) : (
                    <Search size={20} />
                  )}
                </button>

                {!showSearch && (
                  <span className="text-white font-semibold text-lg px-4 py-4">
                    Search
                  </span>
                )}
              </div>

              {/* Glow effect */}
              <div className="absolute inset-0 bg-gradient-to-r from-purple-600 to-pink-600 rounded-full blur-xl opacity-30 group-hover:opacity-50 transition-all duration-500" />
            </div>
          </>
        )}
      </div>

      <style jsx>{`
        @keyframes fade-in {
          from {
            opacity: 0;
            transform: translateY(30px);
          }
          to {
            opacity: 1;
            transform: translateY(0);
          }
        }

        .animate-fade-in {
          animation: fade-in 0.7s ease-out;
        }
      `}</style>
    </div>
  );
}

export default App;
